/* Verify that amomaxu.w instruction is generated by builtin function.  */

/* { dg-do compile } */
/* { dg-require-effective-target riscv_atomic } */
/* { dg-final { scan-assembler "\tamomaxu.w\t" } } */

#include <nds_intrinsic.h>

void
func (void)
{
  /* We want to perform an atomic unsigned min operation.  */
  unsigned int data, cmpv, oldv;
  cmpv = 0x22334455;
  /* new value: unsigned maximum (data, cmpv)  */
  oldv = __nds__amomaxuw (cmpv, &data, UNORDER);
}
