/* Verify that csrrw and csrrwi instruction are generated by builtin function.  */

/* { dg-do compile } */
/* { dg-final { scan-assembler-times "\tcsrrw\t" 1 } } */
/* { dg-final { scan-assembler-times "\tcsrrwi\t" 1 } } */

#include <nds_intrinsic.h>

void
func (unsigned long src)
{
  unsigned long data;
  data = __nds__csrrw (src, 0x304);
}

void
func_i (void)
{
  unsigned long data;
  data = __nds__csrrw (0x1, 0x304);
}
