/* Verify that csrs and csrsi instruction are generated by builtin function.  */

/* { dg-do compile } */
/* { dg-options "-O1" } */
/* { dg-final { scan-assembler-times "\tcsrs\t" 1 } } */
/* { dg-final { scan-assembler-times "\tcsrsi\t" 1 } } */

#include <nds_intrinsic.h>

void
func (void)
{
  unsigned long src = 0x123;
  __nds__csrs (src, 0x456);
}

void
func_i (void)
{
  unsigned long src = 0x123;
  __nds__csrs (src, 0x4);
}
